mapper.insert:
insert into user_auth (id, username, platform
  )
values (#{id,jdbcType=BIGINT}, #{username,jdbcType=VARCHAR}, #{platform,jdbcType=INTEGER}
  )

mapper.update:
update user_auth
set username = #{username,jdbcType=VARCHAR},
  platform = #{platform,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, username, platform
from user_auth
where id = #{id,jdbcType=BIGINT}
